m <- function(a,b){  while((a>0)&(b>0)){    if(craps()==1) {      a=a+1      b=b-1}    else {      a=a-1      b=b+1}  }   if (a==0) {return(0)}  else if(b==0){return(1)}}sum(replicate(1000,m(12,9)))/1000  sum(replicate(1000,m(20,9)))/1000   
