Large <- function(mat){  n <- nrow(mat)  for (i in n:1){    for (j in 1:(n-i+1)){      for (k in 1:(n-i+1)){        A <- matrix(1,i,i)         if(sum(mat[j:(j+i-1),k:(k+i-1)]==A)==i^2) {          return(i^2)        }      }    }  }}
